import React from "react";
import "./styles.css";

const App = () => {
  const arduinoCards = [
    {
      id: 1,
      title: "U",
      code: "void loop() {\n  digitalWrite(13, HIGH);\n  delay(1000);\n}"
    },
    {
      id: 2,
      title: "Analog Read",
      code: "int sensorValue = analogRead(A0);\nSerial.println(sensorValue);"
    },
    {
      id: 3,
      title: "Serial Read",
      code: "if (Serial.available() > 0) {\n  int data = Serial.read();\n}"
    },
    {
      id: 4,
      title: "Servo Control",
      code: "#include <Servo.h>\nServo myservo;\nmyservo.write(90);"
    },
    {
      id: 5,
      title: "Digital Write",
      code: "digitalWrite(7, HIGH);\ndelay(500);\ndigitalWrite(7, LOW);"
    }
  ];

  const piCards = [
    {
      id: 1,
      title: "Ultrasonic Sensor",
      code: `import RPi.GPIO as GPIO
import time

TRIG_PIN = 23
ECHO_PIN = 24

GPIO.setmode(GPIO.BCM)
GPIO.setup(TRIG_PIN, GPIO.OUT)
GPIO.setup(ECHO_PIN, GPIO.IN)

def get_distance():
    GPIO.output(TRIG_PIN, False)
    time.sleep(0.2)
    GPIO.output(TRIG_PIN, True)
    time.sleep(0.00001)
    GPIO.output(TRIG_PIN, False)

    while GPIO.input(ECHO_PIN) == 0:
        pulse_start = time.time()

    while GPIO.input(ECHO_PIN) == 1:
        pulse_end = time.time()

    pulse_duration = pulse_end - pulse_start
    distance = pulse_duration * 17150
    distance = round(distance, 2)
    return distance

try:
    while True:
        dist = get_distance()
        print(f"Distance: {dist} cm")
        time.sleep(1)
except:
    GPIO.cleanup()`
    },
    {
      id: 2,
      title: "LED Control",
      code: "from gpiozero import LED\nled = LED(17)\nled.on()"
    },
    {
      id: 3,
      title: "Button Input",
      code: "from gpiozero import Button\nbutton = Button(2)\nbutton.wait_for_press()"
    },
    {
      id: 4,
      title: "PWM Output",
      code: "import RPi.GPIO as GPIO\nGPIO.setmode(GPIO.BCM)\nGPIO.setup(18, GPIO.OUT)\npwm = GPIO.PWM(18, 100)"
    },
    {
      id: 5,
      title: "Servo Control",
      code: "from gpiozero import Servo\nservo = Servo(17)\nservo.mid()"
    }
  ];

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert("Code copied!");
  };

  return (
    <div className="container">
      <div className="row">
        <h2 className="row-title">Arduino Examples</h2>
        <div className="cards-row">
          {arduinoCards.map((card) => (
            <div key={card.id} className="card arduino" onClick={() => handleCopy(card.code)}>
              <h3>{card.title}</h3>
              <pre><code>{card.code}</code></pre>
            </div>
          ))}
        </div>
      </div>
      <div className="row">
        <h2 className="row-title">Raspberry Pi Examples</h2>
        <div className="cards-row">
          {piCards.map((card) => (
            <div key={card.id} className="card raspberry-pi" onClick={() => handleCopy(card.code)}>
              <h3>{card.title}</h3>
              <pre><code>{card.code}</code></pre>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
