import React from "react";
import "./styles.css";

const App = () => {
  const arduinoCards = [
    {
      id: 1,
      title: "UltraSonic Sensor{THINGSPEAK}",
      code: `#include <WiFi.h>
#include "ThingSpeak.h"

#define SECRET_SSID "YourWiFiSSID"
#define SECRET_PASS "YourWiFiPassword"
#define SECRET_CH_ID 1234567
#define SECRET_WRITE_APIKEY "YOUR_API_KEY"

char ssid[] = SECRET_SSID;
char pass[] = SECRET_PASS;
WiFiClient client;
unsigned long myChannelNumber = SECRET_CH_ID;
const char *myWriteAPIKey = SECRET_WRITE_APIKEY;

#define TRIG_PIN 22
#define ECHO_PIN 23
#define SOUND_VELOCITY 0.034
#define CM_TO_INCH 0.393701

void setup() {
    Serial.begin(115200);
    pinMode(TRIG_PIN, OUTPUT);
    pinMode(ECHO_PIN, INPUT);
    WiFi.mode(WIFI_STA);
    WiFi.begin(ssid, pass);
    while (WiFi.status() != WL_CONNECTED) {
        delay(500);
    }
    ThingSpeak.begin(client);
}

void loop() {
    digitalWrite(TRIG_PIN, LOW);
    delayMicroseconds(2);
    digitalWrite(TRIG_PIN, HIGH);
    delayMicroseconds(10);
    digitalWrite(TRIG_PIN, LOW);

    long duration = pulseIn(ECHO_PIN, HIGH);
    float distanceCm = duration * SOUND_VELOCITY / 2;
    float distanceInch = distanceCm * CM_TO_INCH;

    Serial.print("Distance (cm): ");
    Serial.println(distanceCm);
    Serial.print("Distance (inch): ");
    Serial.println(distanceInch);

    if (WiFi.status() != WL_CONNECTED) {
        WiFi.begin(ssid, pass);
        delay(5000);
        return;
    }

    ThingSpeak.setField(1, distanceCm);
    ThingSpeak.setField(2, distanceInch);

    int x = ThingSpeak.writeFields(myChannelNumber, myWriteAPIKey);
    if (x == 200) {
        Serial.println("Channel update successful.");
    } else {
        Serial.print("Problem updating channel. HTTP error code: ");
        Serial.println(x);
    }
    delay(20000);
}`
    },
    {
      id: 2,
      title: "DHT11",
      code: `#include <WiFi.h>
#include "ThingSpeak.h"
#include "DHT.h"

#define SECRET_SSID "YourWiFiSSID"
#define SECRET_PASS "YourWiFiPassword"
#define SECRET_CH_ID 1234567
#define SECRET_WRITE_APIKEY "YOUR_API_KEY"

char ssid[] = SECRET_SSID;
char pass[] = SECRET_PASS;
WiFiClient client;
unsigned long myChannelNumber = SECRET_CH_ID;
const char *myWriteAPIKey = SECRET_WRITE_APIKEY;

#define DHTPIN 4
#define DHTTYPE DHT11

DHT dht(DHTPIN, DHTTYPE);

void setup() {
    Serial.begin(115200);
    dht.begin();
    WiFi.mode(WIFI_STA);
    WiFi.begin(ssid, pass);
    while (WiFi.status() != WL_CONNECTED) {
        delay(500);
    }
    ThingSpeak.begin(client);
}

void loop() {
    float h = dht.readHumidity();
    float t = dht.readTemperature();

    if (isnan(h) || isnan(t)) {
        Serial.println("Failed to read from DHT sensor!");
        delay(2000);
        return;
    }

    Serial.print("Humidity: ");
    Serial.print(h);
    Serial.print(" %\t");
    Serial.print("Temperature: ");
    Serial.println(t);

    if (WiFi.status() != WL_CONNECTED) {
        WiFi.begin(ssid, pass);
        delay(5000);
        return;
    }

    ThingSpeak.setField(1, h);
    ThingSpeak.setField(2, t);

    int x = ThingSpeak.writeFields(myChannelNumber, myWriteAPIKey);
    if (x == 200) {
        Serial.println("Channel update successful.");
    } else {
        Serial.print("Problem updating channel. HTTP error code: ");
        Serial.println(x);
    }
    delay(20000);
}`
    },
    {
      id: 3,
      title: "Serial Read",
      code: "if (Serial.available() > 0) {\n  int data = Serial.read();\n}"
    },
    {
      id: 4,
      title: "Servo Control",
      code: "#include <Servo.h>\nServo myservo;\nmyservo.write(90);"
    },
    {
      id: 5,
      title: "Digital Write",
      code: "digitalWrite(7, HIGH);\ndelay(500);\ndigitalWrite(7, LOW);"
    }
  ];

  const piCards = [
    {
      id: 1,
      title: "Ultrasonic Sensor",
      code: `import RPi.GPIO as GPIO...` // code content hidden for brevity
    },
    {
      id: 2,
      title: "LED Control",
      code: "from gpiozero import LED\nled = LED(17)\nled.on()"
    },
    {
      id: 3,
      title: "Button Input",
      code: "from gpiozero import Button\nbutton = Button(2)\nbutton.wait_for_press()"
    },
    {
      id: 4,
      title: "PWM Output",
      code: "import RPi.GPIO as GPIO\nGPIO.setmode(GPIO.BCM)\nGPIO.setup(18, GPIO.OUT)\npwm = GPIO.PWM(18, 100)"
    },
    {
      id: 5,
      title: "Servo Control",
      code: "from gpiozero import Servo\nservo = Servo(17)\nservo.mid()"
    }
  ];

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert("Code copied!");
  };

  return (
    <div className="container">
      <div className="row">
        <h2 className="row-title">Arduino Examples</h2>
        <div className="cards-row">
          {arduinoCards.map((card) => (
            <div key={card.id} className="card arduino" onClick={() => handleCopy(card.code)}>
              <h3>{card.title}</h3>
            </div>
          ))}
        </div>
      </div>
      <div className="row">
        <h2 className="row-title">Raspberry Pi Examples</h2>
        <div className="cards-row">
          {piCards.map((card) => (
            <div key={card.id} className="card raspberry-pi" onClick={() => handleCopy(card.code)}>
              <h3>{card.title}</h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
