import React from "react";
import "./styles.css";

const App = () => {
  const cards = Array.from({ length: 10 }, (_, index) => ({
    id: index + 1,
    arduinoCode: `Arduino Code ${index + 1}`,
    raspberryPiCode: `Raspberry Pi Code ${index + 1}`,
  }));

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert(`Copied: ${text}`);
  };

  return (
    <div className="app">
      {cards.map((card) => (
        <div key={card.id} className="card">
          <div
            className="card-section arduino"
            onClick={() => handleCopy(card.arduinoCode)}
          >
            <h2>Arduino</h2>
            <p>{card.arduinoCode}</p>
          </div>
          <div
            className="card-section raspberry-pi"
            onClick={() => handleCopy(card.raspberryPiCode)}
          >
            <h2>Raspberry Pi</h2>
            <p>{card.raspberryPiCode}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
