import React from "react";
import "./styles.css";

const App = () => {
  const arduinoCards = [
    { id: "a1", title: "ultrasonic", code: `#include <WiFi.h>...` },
    { id: "a2", title: "ar", code: "int sensorValue = analogRead(A0);" },
    { id: "a3", title: "sm", code: "..." },
    { id: "a4", title: "ws", code: "..." },
    { id: "a5", title: "dw", code: "digitalWrite(7, HIGH);" }
  ];

  const piCards = [
    { id: "p1", title: "us", code: `import RPi.GPIO as GPIO...` },
    { id: "p2", title: "ld", code: "from gpiozero import LED" },
    { id: "p3", title: "bt", code: "from gpiozero import Button" },
    { id: "p4", title: "pw", code: "import RPi.GPIO as GPIO" },
    { id: "p5", title: "sv", code: "..." }
  ];

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  return (
    <div className="container">
      <div className="cards-row">
        <div className="pi-group">
          {piCards.map((card) => (
            <span 
              key={card.id}
              className="card"
              onClick={() => handleCopy(card.code)}
            >
              {card.title}
            </span>
          ))}
        </div>
        <div className="arduino-group">
          {arduinoCards.map((card) => (
            <span 
              key={card.id}
              className="card"
              onClick={() => handleCopy(card.code)}
            >
              {card.title}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
